import React from "react";
import axios from "axios";
import { Button } from "react-bootstrap";

const VolunteerInfo = () => {
  const [volunteerInfo, setVolunteerInfo] = React.useState();
  const [show, setShow] = React.useState(false);
  const handleShow = () => setShow(true);

  const volunteersInfoFetch = () => {
    axios.get(`http://localhost:3001/volunteers`).then((res) => {
      const persons = res.data;
      setVolunteerInfo(persons);
    });
  };

  React.useEffect(() => {
    volunteersInfoFetch();
  }, []);

  return (
    volunteerInfo &&
    volunteerInfo.map((volunteer) => {
      return (
        <tr key={volunteer.id}>
          <td>{volunteer.id}</td>
          <td>{volunteer.name}</td>
          <td>{volunteer.languagesSpoken}</td>
          <td>{volunteer.occupation}</td>
          <td>{volunteer.skills}</td>
          <td>{volunteer.role}</td>
          <td>
            <Button onClick={handleShow}>Edit</Button>
          </td>
        </tr>
      );
    })
  );
};

export default VolunteerInfo;
