import React from "react";
import { Button, Modal } from "react-bootstrap";
import VolunteerForm from "../VolunteerForm";
import { patchVolunteerInfo } from "../../actions/index";
import { useDispatch, useSelector } from "react-redux";

const VolunteerInfo = ({ volunteer }) => {
  const [show, setShow] = React.useState(false);
  const dispatch = useDispatch();
  const volunteerInfo = useSelector((state) => state.volunteers);

  const handleEdit = (e, id) => {
    e.preventDefault();
    dispatch(patchVolunteerInfo(id));
  };

  return (
    <>
      <tr>
        <td>{volunteer.id}</td>
        <td>{volunteer.name}</td>
        <td>{volunteer.languagesSpoken}</td>
        <td>{volunteer.occupation}</td>
        <td>{volunteer.skills}</td>
        <td>{volunteer.role}</td>
        <td>
          <Button onClick={() => setShow(true)}>Edit</Button>
        </td>
      </tr>
      <Modal show={show} onHide={() => setShow(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <VolunteerForm showButton={false} volunteer={volunteer} />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShow(false)}>
            Close
          </Button>
          <Button
            variant="primary"
            onClick={(e) => {
              setShow(false);
              handleEdit(e, volunteer.id);
            }}
          >
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default VolunteerInfo;
