import React from "react";
import { Table, Container, Button, Modal } from "react-bootstrap";
import axios from "axios";
import VolunteerForm from "../VolunteerForm";

const VolunteerTable = () => {
  const [volunteerInfo, setVolunteerInfo] = React.useState();
  const [show, setShow] = React.useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const volunteersInfo = () => {
    axios.get(`http://localhost:3001/volunteers`).then((res) => {
      const persons = res.data;
      setVolunteerInfo(persons);
    });
  };

  React.useEffect(() => {
    volunteersInfo();
  }, []);

  const handleEdit = (e, id) => {
    e.preventDefault();
    handleClose();
    // axios
    //   .patch(`http://localhost:3001/volunteers/${id}`, {
    //     // name: name,
    //     // languagesSpoken: languagesSpoken,
    //     // occupation: occupation,
    //     // skills: skills,
    //     // role: role,
    //   })
    //   .then(function (response) {
    //     console.log(response);
    //   })
    //   .catch(function (error) {
    //     console.log(error);
    //   });
    console.log(id);
  };

  return (
    <Container>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Language</th>
            <th>Occupation</th>
            <th>Skills</th>
            <th>Role</th>
          </tr>
        </thead>
        <tbody>
          {volunteerInfo &&
            volunteerInfo.map((volunteer) => {
              return (
                <>
                  <tr key={volunteer.id}>
                    <td>{volunteer.id}</td>
                    <td>{volunteer.name}</td>
                    <td>{volunteer.languagesSpoken}</td>
                    <td>{volunteer.occupation}</td>
                    <td>{volunteer.skills}</td>
                    <td>{volunteer.role}</td>
                    <td>
                      <Button onClick={handleShow}>Edit</Button>
                    </td>
                  </tr>
                  <Modal show={show} onHide={handleClose}>
                    <Modal.Header closeButton>
                      <Modal.Title>Modal heading</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                      <VolunteerForm showButton={false} />
                    </Modal.Body>
                    <Modal.Footer>
                      <Button variant="secondary" onClick={handleClose}>
                        Close
                      </Button>
                      <Button
                        variant="primary"
                        onClick={(e) => handleEdit(e, volunteer.id)}
                      >
                        Save Changes
                      </Button>
                    </Modal.Footer>
                  </Modal>
                </>
              );
            })}
        </tbody>
      </Table>
    </Container>
  );
};

export default VolunteerTable;
