import React from "react";
import { Table, Container } from "react-bootstrap";
import axios from "axios";
import VolunteerInfo from "../VolunteerInfo";

const VolunteerTable = () => {
  const [volunteerInfo, setVolunteerInfo] = React.useState();

  const volunteersInfo = () => {
    axios.get(`http://localhost:3001/volunteers`).then((res) => {
      const persons = res.data;
      setVolunteerInfo(persons);
    });
  };

  React.useEffect(() => {
    volunteersInfo();
  }, []);

  const handleEdit = (e, volunteer) => {
    e.preventDefault();
    axios
      .patch(`http://localhost:3001/volunteers/${volunteer.id}`, {
        name: volunteer.name,
        languagesSpoken: volunteer.languagesSpoken,
        occupation: volunteer.occupation,
        skills: volunteer.skills,
        role: volunteer.role,
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  return (
    <Container>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Language</th>
            <th>Occupation</th>
            <th>Skills</th>
            <th>Role</th>
          </tr>
        </thead>
        <tbody>
          {volunteerInfo &&
            volunteerInfo.map((volunteer) => {
              return (
                <>
                  <VolunteerInfo
                    volunteer={volunteer}
                    handleEdit={handleEdit}
                  />
                </>
              );
            })}
        </tbody>
      </Table>
    </Container>
  );
};

export default VolunteerTable;
