import React from "react";
import { Form, Container, Button } from "react-bootstrap";
import { postVolunteerInfo } from "../../actions/index";
import { useDispatch, useSelector } from "react-redux";
// import axios from "axios";

const VolunteerForm = ({ showButton, volunteer }) => {
  const dispatch = useDispatch();
  const volunteerInfo = useSelector((state) => state.volunteers);

  const [name, setName] = React.useState(volunteer ? volunteer.name : "");
  const [languagesSpoken, setLanguagesSpoken] = React.useState(
    volunteer ? volunteer.languagesSpoken : ""
  );
  const [occupation, setOccupation] = React.useState(
    volunteer ? volunteer.occupation : ""
  );
  const [skills, setSkills] = React.useState(volunteer ? volunteer.skills : "");
  const [role, setRole] = React.useState(volunteer ? volunteer.role : "");

  // const handleSubmit = (e) => {
  //   e.preventDefault();
  //   axios
  //     .post("http://localhost:3001/volunteers", {
  //       name: name,
  //       languagesSpoken: languagesSpoken,
  //       occupation: occupation,
  //       skills: skills,
  //       role: role,
  //     })
  //     .then(function (response) {
  //       console.log(response);
  //     })
  //     .catch(function (error) {
  //       console.log(error);
  //     });
  // };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(
      postVolunteerInfo(name, role, languagesSpoken, occupation, skills)
    );
  };

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleLanguagesSpokenChange = (e) => {
    setLanguagesSpoken(e.target.value);
  };

  const handleSkillsChange = (e) => {
    setSkills(e.target.value);
  };

  const handleOccupationChange = (e) => {
    setOccupation(e.target.value);
  };

  const handleRoleChange = (e) => {
    setRole(e.target.value);
  };

  return (
    <Container>
      <Form onSubmit={(e) => handleSubmit(e)}>
        <Form.Group>
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter name"
            value={name}
            onChange={(e) => handleNameChange(e)}
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Language</Form.Label>
          <Form.Control
            value={languagesSpoken}
            type="text"
            placeholder="Enter languages spoken"
            onChange={(e) => handleLanguagesSpokenChange(e)}
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>Occupation</Form.Label>
          <Form.Control
            value={occupation}
            type="text"
            placeholder="Enter occupation"
            onChange={(e) => handleOccupationChange(e)}
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>Skills</Form.Label>
          <Form.Control
            value={skills}
            type="text"
            placeholder="Enter skills"
            onChange={(e) => handleSkillsChange(e)}
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>Role</Form.Label>
          <Form.Control
            value={role}
            type="text"
            placeholder="Enter role"
            onChange={(e) => handleRoleChange(e)}
          />
        </Form.Group>
        {showButton && (
          <Button variant="primary" type="submit">
            Submit
          </Button>
        )}
      </Form>
    </Container>
  );
};

export default VolunteerForm;
