import React from "react";
import { Button } from "react-bootstrap";

const VolunteerInfo = ({ volunteer, handleEdit }) => {
  const [show, setShow] = React.useState(false);
  console.log(volunteer);

  const handleShow = () => {
    setShow(true);
  };
  return (
    <>
      <tr>
        <td>{volunteer.id}</td>
        <td>{volunteer.name}</td>
        <td>{volunteer.languagesSpoken}</td>
        <td>{volunteer.occupation}</td>
        <td>{volunteer.skills}</td>
        <td>{volunteer.role}</td>
        <td>
          <Button onClick={handleShow}>Edit</Button>
        </td>
      </tr>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <VolunteerForm showButton={false} volunteer={volunteer} />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={(e) => handleEdit(e, volunteer)}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default VolunteerInfo;
